mapper.select:


select * from (select A.*, ROWNUM as RNUM
from(select * from reserve order by rdate)A)
where RNUM < 11



select * from (select A.*, ROWNUM as RNUM
from(select * from reserve order by rdate)A)
where RNUM >= #{startNo} and RNUM <= #{startNo} + 9


select * from (select A.*, ROWNUM as RNUM
from(select * from reserve order by rdate)A where rdate = #{rDate})
where  RNUM < 11


select * from (select A.*, ROWNUM as RNUM
from(select * from reserve order by rdate)A where rdate = #{rDate} )
where RNUM >= #{startNo} and RNUM <= #{startNo} + 9
